-        String response = getSauceAPIFactory().doREST(VERSION_CHECK_URL);
-//        String response = IOUtils.toString(getClass().getResourceAsStream("/versions.json"));
-        File jarFile = retrieveNewVersion(response);
-        updatePluginJar(jarFile);
-    }
